import React, { useState, useEffect } from 'react';
import { Tabs, Tab, Card, Button, Table, Spinner, Alert, Row, Col, Form } from 'react-bootstrap';
import { saveAs } from 'file-saver';

const categorias = [
  { key: 'laboratorio_central', label: 'Laboratorio Central' },
  { key: 'campo', label: 'Inventario de Campo' },
  { key: 'consumible_lab_central', label: 'Consumible en Laboratorio Central' },
  { key: 'consumible_seguridad', label: 'Consumible por Equipo de Seguridad' },
  { key: 'laboratorio_campo', label: 'Laboratorio de Campo' }
];

function Reportes() {
  const [equipos, setEquipos] = useState([]);
  const [documentos, setDocumentos] = useState([]);
  const [loading, setLoading] = useState(false);
  const [mensaje, setMensaje] = useState('');

  useEffect(() => {
    async function fetchEquipos() {
      try {
        const res = await fetch('/api/equipos');
        const data = await res.json();
        setEquipos(data);
      } catch (error) {
        setMensaje('Error al cargar los equipos');
      }
    }
    async function fetchDocumentos() {
      try {
        const res = await fetch('/api/documentos/all');
        const data = await res.json();
        setDocumentos(data);
      } catch (error) {
        setMensaje('Error al cargar los documentos');
      }
    }
    fetchEquipos();
    fetchDocumentos();
  }, []);

  // Exportar inventario por categorÃ­a
  const exportarExcelCategoria = async (categoria) => {
    setLoading(true);
    const params = new URLSearchParams({ categoria }).toString();
    const response = await fetch(`/api/reportes/inventario/excel/filtrado?${params}`);
    const blob = await response.blob();
    saveAs(blob, `inventario_${categoria}.xlsx`);
    setLoading(false);
  };
  const exportarPDFCategoria = async (categoria) => {
    setLoading(true);
    const params = new URLSearchParams({ categoria }).toString();
    const response = await fetch(`/api/reportes/inventario/pdf/filtrado?${params}`);
    const blob = await response.blob();
    saveAs(blob, `inventario_${categoria}.pdf`);
    setLoading(false);
  };

  // Exportar todos los documentos
  const exportarDocumentosExcel = async () => {
    setLoading(true);
    const response = await fetch(`/api/reportes/documentos/excel`);
    const blob = await response.blob();
    saveAs(blob, 'documentos.xlsx');
    setLoading(false);
  };
  const exportarDocumentosPDF = async () => {
    setLoading(true);
    const response = await fetch(`/api/reportes/documentos/pdf`);
    const blob = await response.blob();
    saveAs(blob, 'documentos.pdf');
    setLoading(false);
  };

  // Descargar todas las fotos
  const exportarFotosZIP = async () => {
    setLoading(true);
    const response = await fetch(`/api/reportes/fotos/zip`);
    const blob = await response.blob();
    saveAs(blob, 'fotos.zip');
    setLoading(false);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Reportes y Descargas</h2>
      {mensaje && <Alert variant="danger">{mensaje}</Alert>}
      <Tabs defaultActiveKey="inventario" className="mb-3" fill>
        <Tab eventKey="inventario" title="Inventario por CategorÃ­as">
          <Row>
            {categorias.map(cat => {
              const equiposCat = equipos.filter(eq => eq.categoria === cat.key);
              return (
                <Col md={6} key={cat.key} className="mb-4">
                  <Card>
                    <Card.Header as="h5">{cat.label}</Card.Header>
                    <Card.Body>
                      <div className="mb-2 d-flex gap-2">
                        <Button variant="outline-success" onClick={() => exportarExcelCategoria(cat.key)} disabled={loading}>
                          {loading ? <Spinner animation="border" size="sm" /> : 'Descargar Excel'}
                        </Button>
                        <Button variant="outline-danger" onClick={() => exportarPDFCategoria(cat.key)} disabled={loading}>
                          {loading ? <Spinner animation="border" size="sm" /> : 'Descargar PDF'}
                        </Button>
                      </div>
                      <Table striped bordered hover size="sm">
                        <thead>
                          <tr>
                            <th>CategorÃ­a</th>
                            <th>SubcategorÃ­a</th>
                            <th>Existencia</th>
                            <th>Equipo</th>
                            <th>DescripciÃ³n</th>
                            <th>Marca</th>
                            <th>Modelo</th>
                          </tr>
                        </thead>
                        <tbody>
                          {equiposCat.length === 0 ? (
                            <tr><td colSpan={7} className="text-center text-muted">No hay equipos registrados.</td></tr>
                          ) : (
                            equiposCat.map(eq => (
                              <tr key={eq.id}>
                                <td>{eq.categoria}</td>
                                <td>{eq.subcategoria}</td>
                                <td>{eq.existencia}</td>
                                <td>{eq.nombre}</td>
                                <td>{eq.descripcion}</td>
                                <td>{eq.marca}</td>
                                <td>{eq.modelo}</td>
                              </tr>
                            ))
                          )}
                        </tbody>
                      </Table>
                    </Card.Body>
                  </Card>
                </Col>
              );
            })}
          </Row>
        </Tab>
        <Tab eventKey="documentos" title="Reportes de Documentos">
          <Card>
            <Card.Header as="h5">Todos los Documentos</Card.Header>
            <Card.Body>
              <div className="mb-2 d-flex gap-2">
                <Button variant="outline-success" onClick={exportarDocumentosExcel} disabled={loading}>
                  {loading ? <Spinner animation="border" size="sm" /> : 'Descargar Excel'}
                </Button>
                <Button variant="outline-danger" onClick={exportarDocumentosPDF} disabled={loading}>
                  {loading ? <Spinner animation="border" size="sm" /> : 'Descargar PDF'}
                </Button>
              </div>
              <Table striped bordered hover size="sm">
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Equipo</th>
                    <th>Tipo</th>
                    <th>Archivo</th>
                    <th>Fecha de subida</th>
                    <th>Fecha de vencimiento</th>
                  </tr>
                </thead>
                <tbody>
                  {documentos.length === 0 ? (
                    <tr><td colSpan={6} className="text-center text-muted">No hay documentos registrados.</td></tr>
                  ) : (
                    documentos.map(doc => (
                      <tr key={doc.id}>
                        <td>{doc.id}</td>
                        <td>{doc.equipo_nombre || doc.equipo_id}</td>
                        <td>{doc.tipo}</td>
                        <td>{doc.url_archivo?.split('/').pop()}</td>
                        <td>{doc.fecha_subida ? doc.fecha_subida.substring(0, 10) : ''}</td>
                        <td>{doc.fecha_vencimiento ? doc.fecha_vencimiento.substring(0, 10) : 'N/A'}</td>
                      </tr>
                    ))
                  )}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        </Tab>
        <Tab eventKey="fotos" title="GalerÃ­a de Fotos">
          <Card>
            <Card.Header as="h5">Fotos Subidas</Card.Header>
            <Card.Body>
              <div className="mb-2 d-flex gap-2">
                <Button variant="outline-primary" onClick={exportarFotosZIP} disabled={loading}>
                  {loading ? <Spinner animation="border" size="sm" /> : 'Descargar Todas (ZIP)'}
                </Button>
              </div>
              <Row>
                {documentos.filter(doc => doc.tipo === 'foto').length === 0 ? (
                  <Col className="text-muted">No hay fotos subidas.</Col>
                ) : (
                  documentos.filter(doc => doc.tipo === 'foto').map(doc => (
                    <Col md={3} key={doc.id} className="mb-3">
                      <Card>
                        <Card.Img variant="top" src={`/${doc.url_archivo}`} style={{ maxHeight: 180, objectFit: 'cover' }} />
                        <Card.Body>
                          <Button variant="outline-secondary" size="sm" href={`/${doc.url_archivo}`} download>
                            Descargar
                          </Button>
                        </Card.Body>
                      </Card>
                    </Col>
                  ))
                )}
              </Row>
            </Card.Body>
          </Card>
        </Tab>
      </Tabs>
    </div>
  );
}

export default Reportes;
