import React, { useState } from 'react';
import { Card, Button, Row, Col, Form, Alert, Spinner } from 'react-bootstrap';

function Reportes() {
  const [loading, setLoading] = useState(false);
  const [mensaje, setMensaje] = useState({ tipo: '', texto: '' });
  const [filtros, setFiltros] = useState({
    ubicacion: '',
    estado: '',
    categoria: ''
  });

  const descargarReporte = async (tipo, formato, filtros = null) => {
    setLoading(true);
    setMensaje({ tipo: '', texto: '' });

    try {
      let url = `/api/reportes/${tipo}/${formato}`;
      
      // Agregar filtros si existen
      if (filtros) {
        const params = new URLSearchParams();
        Object.keys(filtros).forEach(key => {
          if (filtros[key]) {
            params.append(key, filtros[key]);
          }
        });
        if (params.toString()) {
          url += `?${params.toString()}`;
        }
      }

      // Crear un enlace temporal para descargar
      const link = document.createElement('a');
      link.href = url;
      link.download = `${tipo}_${formato}_${new Date().toISOString().split('T')[0]}.${formato}`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

      setMensaje({ tipo: 'success', texto: `Reporte ${tipo} descargado correctamente` });
    } catch (error) {
      console.error('Error al descargar reporte:', error);
      setMensaje({ tipo: 'danger', texto: 'Error al descargar el reporte' });
    } finally {
      setLoading(false);
    }
  };

  const handleFiltroChange = (campo, valor) => {
    setFiltros(prev => ({
      ...prev,
      [campo]: valor
    }));
  };

  return (
    <div>
      <h2 className="mb-4">Reportes de Inventario</h2>

      {mensaje.texto && (
        <Alert variant={mensaje.tipo} dismissible onClose={() => setMensaje({ tipo: '', texto: '' })}>
          {mensaje.texto}
        </Alert>
      )}

      <Row>
        {/* Reporte de Inventario Completo */}
        <Col md={6} className="mb-4">
          <Card>
            <Card.Header>
              <h5 className="mb-0">Inventario Completo</h5>
            </Card.Header>
            <Card.Body>
              <p className="text-muted">
                Descarga el inventario completo de todos los equipos
              </p>
              <div className="d-grid gap-2">
                <Button 
                  variant="outline-success" 
                  onClick={() => descargarReporte('inventario', 'excel')}
                  disabled={loading}
                >
                  {loading ? <Spinner animation="border" size="sm" /> : 'ðŸ“Š Descargar Excel'}
                </Button>
                <Button 
                  variant="outline-danger" 
                  onClick={() => descargarReporte('inventario', 'pdf')}
                  disabled={loading}
                >
                  {loading ? <Spinner animation="border" size="sm" /> : 'ðŸ“„ Descargar PDF'}
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>

        {/* Reporte Filtrado */}
        <Col md={6} className="mb-4">
          <Card>
            <Card.Header>
              <h5 className="mb-0">Inventario Filtrado</h5>
            </Card.Header>
            <Card.Body>
              <p className="text-muted">
                Descarga el inventario con filtros especÃ­ficos
              </p>
              
              <Form className="mb-3">
                <Form.Group className="mb-2">
                  <Form.Label>UbicaciÃ³n</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="Ej: Laboratorio A"
                    value={filtros.ubicacion}
                    onChange={(e) => handleFiltroChange('ubicacion', e.target.value)}
                  />
                </Form.Group>

                <Form.Group className="mb-2">
                  <Form.Label>Estado</Form.Label>
                  <Form.Select
                    value={filtros.estado}
                    onChange={(e) => handleFiltroChange('estado', e.target.value)}
                  >
                    <option value="">Todos</option>
                    <option value="activo">Activo</option>
                    <option value="inactivo">Inactivo</option>
                  </Form.Select>
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>CategorÃ­a</Form.Label>
                  <Form.Select
                    value={filtros.categoria}
                    onChange={(e) => handleFiltroChange('categoria', e.target.value)}
                  >
                    <option value="">Todas</option>
                    <option value="campo">Campo</option>
                    <option value="central">Central</option>
                  </Form.Select>
                </Form.Group>
              </Form>

              <div className="d-grid gap-2">
                <Button 
                  variant="outline-success" 
                  onClick={() => descargarReporte('inventario/excel/filtrado', 'excel', filtros)}
                  disabled={loading}
                >
                  {loading ? <Spinner animation="border" size="sm" /> : 'ðŸ“Š Descargar Excel Filtrado'}
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row>
        {/* Reporte por UbicaciÃ³n */}
        <Col md={6} className="mb-4">
          <Card>
