import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Table, Button, Spinner, Alert } from 'react-bootstrap';

function VerDocumentos({ equipoId = 1 }) {
  const [documentos, setDocumentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [mensaje, setMensaje] = useState('');

  useEffect(() => {
    async function fetchDocumentos() {
      setLoading(true);
      try {
        const res = await axios.get(`/api/documentos/equipo/${equipoId}`);
        setDocumentos(res.data);
        setMensaje('');
      } catch (error) {
        setMensaje('Error al cargar los documentos');
      }
      setLoading(false);
    }
    fetchDocumentos();
  }, [equipoId]);

  const descargar = (id) => {
    window.open(`/api/documentos/descargar/${id}`, '_blank');
  };

  return (
    <div>
      <h3>Documentos del Equipo #{equipoId}</h3>
      {mensaje && <Alert variant="danger">{mensaje}</Alert>}
      {loading ? (
        <Spinner animation="border" />
      ) : documentos.length === 0 ? (
        <Alert variant="info">No hay documentos subidos para este equipo.</Alert>
      ) : (
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Tipo</th>
              <th>Archivo</th>
              <th>Fecha de subida</th>
              <th>Fecha de vencimiento</th>
              <th>Acciones</th>
            </tr>
          </thead>
          <tbody>
            {documentos.map(doc => (
              <tr key={doc.id}>
                <td>{doc.id}</td>
                <td>{doc.tipo}</td>
                <td>{doc.url_archivo.split('/').pop()}</td>
                <td>{doc.fecha_subida ? doc.fecha_subida.substring(0, 10) : ''}</td>
                <td>{doc.fecha_vencimiento ? doc.fecha_vencimiento.substring(0, 10) : 'N/A'}</td>
                <td>
                  <Button size="sm" variant="success" onClick={() => descargar(doc.id)}>
                    Descargar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
}

export default VerDocumentos;
